pragma solidity ^0.8.8;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@chainlink/contracts/src/v0.8/interfaces/KeeperCompatibleInterface.sol";
import { UpkeepIDConsumer } from "./UpkeepIDConsumer.sol";
import { LinkTokenInterface } from "@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol";

// errors
error Cmp_NIS(); /**not in state */
error Cmp_NotCrtr();
error Cmp_DIC(); /**donator is creator */
error Cmp_NoDns();
error Cmp_RefF();
error Cmp_UpkNN();
error Cmp_NotRef();
error Cmp_Bankrupt();

contract Campaign is KeeperCompatibleInterface, ReentrancyGuard{
  using SafeMath for uint256;

  // enums
  enum C_State {
    Fundraising,
    Expired,
    Canceled
  }

  enum C_Mode {
    Draft,
    Published
  }

  // c_state variables
  address immutable private i_crf;
  address payable immutable public i_creator;
  string public s_title;
  string public s_description;
  string public s_category;
  string public s_imageURI;
  string public s_campaignURI;
  string public s_tags;
  uint256 public goalAmount;
  uint256 public duration;
  uint256 public currentBalance;
  uint256 private immutable i_initTimeStamp;
  uint256 private constant i_maxDur = 5184000;
  uint256 public deadline;
  C_Mode public c_mode = C_Mode.Draft;
  C_State public c_state = C_State.Fundraising; // default c_state
  uint256 private rId;

  struct CampaignObject {
    address i_creator;
    string s_title;
    string s_description;
    string s_category;
    string s_tags;
    uint256 goalAmount;
    uint256 duration;
    uint256 currentBalance;
    C_State currentC_State;
    string s_imageURI;
    string s_campaignURI;
    uint256 deadline;
  }

  struct reward {
    uint256 price;
    string title;
    string description;
    string[] perks;
    uint256 delDate;
    uint256 quantity;
    bool infinite;
    string[] shipsTo;
  }

  mapping (uint256 => reward) public rewards;
  mapping (address => uint256[]) public donations;
  mapping (address => uint256) public aggrDonations;

  uint256[] public rKeys;


  // events
  event FundingRecieved (
    address indexed contributor,
    uint256 amount,
    uint256 currentBalance
  );
  event CreatorPaid(address creator, address campaignAddress);
  event CampaignSuccessful(address campaignAddress);
  event CampaignExpired(address campaignAddress);
  event CampaignCanceled();


  // modifiers
  modifier isCreator() {
    if(msg.sender != i_creator){revert Cmp_NotCrtr();}
    _;
  }


  constructor (
    address _crowdfunder,
    address _creator,
    string memory _title,
    string memory _description,
    string memory _category,
    string memory _tags,
    uint256 _goalAmount,
    uint256 _duration,
    string memory _imageURI
  ) {
    i_crf = _crowdfunder;
    i_creator = payable(_creator);
    s_title = _title;
    s_description = _description;
    s_category = _category;
    s_tags = _tags;
    goalAmount = _goalAmount;
    i_initTimeStamp = block.timestamp;
    duration = _duration > i_maxDur ? i_maxDur : _duration;
    deadline = i_initTimeStamp.add(duration);
    s_imageURI = _imageURI;
    currentBalance = 0;
  }

  function timeBox(address _upkeepCreatorAddress, address _linkTokenAddress, address _campaignAddress) external isCreator {
    UpkeepIDConsumer newUpkeepCreator = UpkeepIDConsumer(_upkeepCreatorAddress);
    LinkTokenInterface token = LinkTokenInterface(_linkTokenAddress);
    if(token.balanceOf(_upkeepCreatorAddress) == 0){revert("no funds");}
    rId = newUpkeepCreator.registerAndPredictID(s_title, "0x", _campaignAddress, 500000, i_creator, "0x", "0x", 2000000000000000000);
    c_mode = C_Mode.Published;
  }

  function donate(address _donator) public payable nonReentrant{
    if(msg.sender != i_crf){revert();}
    if(c_state != C_State.Fundraising){revert Cmp_NIS();}
    if(_donator == i_creator){revert Cmp_DIC();}
    currentBalance = currentBalance.add(msg.value);
    if(rewards[msg.value].price > 0 && !rewards[msg.value].infinite) //exists and is not infinite
    {
      rewards[msg.value].quantity.sub(1);
      if(rewards[msg.value].quantity == 0){delete(rewards[msg.value]);}
    }
    donations[_donator].push(msg.value);
    aggrDonations[_donator] = aggrDonations[_donator].add(msg.value);
    emit FundingRecieved(_donator, msg.value, currentBalance);
  }

  /**
    @dev this is the function chainlink keepers calls
    chekupkeep returns true to trigger the action after the interval has passed
   */
  function checkUpkeep(bytes memory /**checkData */) public view override
  returns (bool upkeepNeeded, bytes memory /**performData */) 
  {
    bool isOpen = c_state == C_State.Fundraising;
    bool timePassed = ((block.timestamp.sub(i_initTimeStamp)) > duration);
    bool hasBalance = address(this).balance > 0;
    upkeepNeeded = (timePassed && isOpen && hasBalance) ;
    return (upkeepNeeded, "0x0");
  }

  function performUpkeep(bytes calldata /**performData */) external override {
    (bool upkeepNeeded, ) = checkUpkeep("");
    if(!upkeepNeeded){revert Cmp_UpkNN();}
    c_state = C_State.Expired;
    emit CampaignExpired(address(this));
    if(currentBalance >= goalAmount){
      emit CampaignSuccessful(address(this));
    }
  }

  function payout() external isCreator{
    if(c_state != C_State.Expired){revert Cmp_NIS();}
    uint256 totalRaised = currentBalance;
    currentBalance = 0;
    (bool success, ) = i_creator.call{value: totalRaised}("");
    if(success){
      emit CreatorPaid(i_creator, address(this));
    }
    else{revert();}
  }

  function refund(address _donator) external nonReentrant{
    if(msg.sender != i_crf){revert();}
    if(c_state == C_State.Expired){revert Cmp_NIS();}
    if(aggrDonations[_donator] == 0 ){revert Cmp_NoDns();}
    uint256 amountToRefund = aggrDonations[_donator];
    if(currentBalance < amountToRefund){revert Cmp_Bankrupt();}
    currentBalance = currentBalance.sub(amountToRefund);
    (bool success, ) = payable(_donator).call{value: amountToRefund}("");
    if(!success){revert Cmp_RefF();}
    delete(donations[_donator]);
    delete(aggrDonations[_donator]);
  }

  function makeReward( 
    uint256 _price, string memory _title, 
    string memory _description, string[] memory _perks, 
    uint256 _deadline, uint256 _quantity, bool _infinite, string[] memory _shipsTo
    ) external isCreator {
    rKeys.push(_price);
    // shipsto _NW, infinite true, quantitymax 100  (for digRewards)  shipsto _AITW for phyRewards
    rewards[_price] = reward(_price, _title, _description, _perks, _deadline, _quantity, _infinite, _shipsTo);
  }

  function deleteReward(uint256 _priceID) external isCreator {
    if(c_mode != C_Mode.Draft){revert();}
    if(rewards[_priceID].price > 0){delete(rewards[_priceID]);}
  }

  function endCampaign() external isCreator {
    if(c_state == C_State.Expired){revert();}
    c_state = C_State.Canceled;
    emit CampaignCanceled();
  }

  // update functions
  function updateCampaignURI(string memory _campaignURI) external isCreator {
    s_campaignURI = _campaignURI;
  }

  function updateDur(uint256 _addedDur) external isCreator {
    duration = ((duration.add(_addedDur)) > i_maxDur) ? i_maxDur : duration.add(_addedDur);
    deadline = i_initTimeStamp.add(duration);
  }

  // getter functions
  function getDonations(address _donator) external view returns(uint256[] memory) {
    return donations[_donator];
  }

  function getRewardKeys() external view returns(uint256[] memory){
    return rKeys;
  }
  
  function getReward(uint256 _priceID) external view returns (reward memory) {
    return rewards[_priceID];
  }

  function getCampaignDetails() external view returns(CampaignObject memory) {
    return CampaignObject(
      i_creator,
      s_title,
      s_description,
      s_category,
      s_tags,
      goalAmount,
      duration,
      currentBalance,
      c_state,
      s_imageURI,
      s_campaignURI,
      deadline
    );
  }

  // fallback functions
  fallback() external payable {}
  receive() external payable {}
}","keccak256":"0x7ec28148364c11984645434436d8087d7a8a8df2419f20ead1dec5a5ee359da2","license":"MIT"},"contracts/CrowdFunder.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./Campaign.sol";

// errors
error Crf_NotCrtr();
error Crf_CSA(); /** cmp still active */
error Crf_DonF();
error Crf_RefF();
error Crf_PubF();

contract CrowdFunder {
  using SafeMath for uint256;

  event UserAdded(
    address indexed _address,
    string _username,
    string _twitter,
    string _email,
    string _homeAddress,
    string _pfp
  );

  event CampaignAdded(
    address indexed _campaignAddress,
    address indexed _creator,
    string _title,
    string _description,
    string _category,
    string _tags
  );

  event CampaignFunded(
    address indexed _funder,
    address indexed _campaignAddress,
    uint256 _val
  );

  event CampaignShrunk(
    address indexed _withdrawer,
    address indexed _campaignAddress,
    uint256 _val
  );

  event CampaignRemoved(
    address indexed _campaignAddress
  );

  event CampaignPublished(
    address _campaignAddress,
    address _creator
  );

  uint256 public campaignCounter;
  mapping (address => Campaign) private campaigns;

  function addUser(
    address _address, string memory _username, 
    string memory _twitter, string memory _email, 
    string memory _homeAddress,
    string memory _pfp
    ) external {
    emit UserAdded(_address, _username, _twitter, _email, _homeAddress, _pfp);
  }

  function addCampaign (
    string memory _title, 
    string memory _description,
    string memory _category,
    string memory _tags, 
    uint256 _goalAmount,
    uint256 _duration,
    string memory _imageURI
    ) external {
    Campaign newCampaign = new Campaign(
      address(this),
      payable(msg.sender), _title, 
      _description, _category, 
      _tags, _goalAmount, 
      _duration, _imageURI
    );
    campaigns[address(newCampaign)] = newCampaign;
    emit CampaignAdded(address(newCampaign), msg.sender, _title, _description, _category, _tags);
  }

  function donateToCampaign(address _campaignAddress) external payable {
    (bool success, ) = _campaignAddress.call{value:msg.value}(abi.encodeWithSignature("donate(address)",msg.sender));
    if(success){
      emit CampaignFunded(msg.sender, _campaignAddress, msg.value);
    }else{
      revert Crf_DonF();
    }
  }

  function refundFromCampaign(address _campaignAddress) external {
    uint256 refVal = campaigns[_campaignAddress].aggrDonations(msg.sender);
    if(!(refVal > 0)){revert();}
    (bool success,) = _campaignAddress.call(abi.encodeWithSignature("refund(address)", msg.sender));
    // campaigns[_campaignAddress].refund(msg.sender);
    // emit CampaignShrunk(msg.sender, _campaignAddress, refVal);
    if(success){
      emit CampaignShrunk(msg.sender, _campaignAddress, refVal);
    }else{
      revert Crf_RefF();
    }
  }

  function removeCampaign (address _campaignAddress) external {
    if(campaigns[_campaignAddress].i_creator() != msg.sender){revert Crf_NotCrtr();}
    if(campaigns[_campaignAddress].currentBalance() > 0){revert Crf_CSA();}
    // either payout or leave for refunds
    delete(campaigns[_campaignAddress]);
    emit CampaignRemoved(_campaignAddress);
  }

  function publishCampaign(address _campaignAddress, address _upkeepCreator, address _linkToken) external {
    (bool success, ) = _campaignAddress.delegatecall(abi.encodeWithSignature("timeBox(address,address,address)", _upkeepCreator, _linkToken, _campaignAddress));
    if(success){
      campaignCounter = campaignCounter.add(1);
      emit CampaignPublished(_campaignAddress, msg.sender);
    }else{revert Crf_PubF();}
  }

  fallback() external payable{}
  receive() external payable{}
}","keccak256":"0x6ae78101cddf581b4a4c7abca2357c2d82567c55c89eb03b79032098eb48a7a7","license":"MIT"},"contracts/UpkeepIDConsumer.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;

// UpkeepIDConsumerExample.sol imports functions from both ./AutomationRegistryInterface2_0.sol and
// ./interfaces/LinkTokenInterface.sol

import {AutomationRegistryInterface, State, OnchainConfig} from "@chainlink/contracts/src/v0.8/interfaces/AutomationRegistryInterface2_0.sol";
import {LinkTokenInterface} from "@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol";

/**
 * THIS IS AN EXAMPLE CONTRACT THAT USES HARDCODED VALUES FOR CLARITY.
 * THIS IS AN EXAMPLE CONTRACT THAT USES UN-AUDITED CODE.
 * DO NOT USE THIS CODE IN PRODUCTION.
 */

interface KeeperRegistrarInterface {
    function register(
        string memory name,
        bytes calldata encryptedEmail,
        address upkeepContract,
        uint32 gasLimit,
        address adminAddress,
        bytes calldata checkData,
        bytes calldata offchainConfig,
        uint96 amount,
        address sender
    ) external;
}

contract UpkeepIDConsumer {
    LinkTokenInterface public immutable i_link;
    address public immutable registrar;
    AutomationRegistryInterface public immutable i_registry;
    bytes4 registerSig = KeeperRegistrarInterface.register.selector;

    constructor(
        LinkTokenInterface _link,
        address _registrar,
        AutomationRegistryInterface _registry
    ) {
        i_link = _link;
        registrar = _registrar;
        i_registry = _registry;
    }

    function registerAndPredictID(
        string memory name,
        bytes calldata encryptedEmail,
        address upkeepContract,
        uint32 gasLimit,
        address adminAddress,
        bytes calldata checkData,
        bytes calldata offchainConfig,
        uint96 amount
    ) public returns(uint256) {
        (State memory state, , , , ) = i_registry.getState();
        uint256 oldNonce = state.nonce;
        bytes memory payload = abi.encode(
            name,
            encryptedEmail,
            upkeepContract,
            gasLimit,
            adminAddress,
            checkData,
            offchainConfig,
            amount,
            address(this)
        );

        i_link.transferAndCall(
            registrar,
            amount,
            bytes.concat(registerSig, payload)
        );
        (state, , , , ) = i_registry.getState();
        uint256 newNonce = state.nonce;
        if (newNonce == oldNonce + 1) {
            uint256 upkeepID = uint256(
                keccak256(
                    abi.encodePacked(
                        blockhash(block.number - 1),
                        address(i_registry),
                        uint32(oldNonce)
                    )
                )
            );
            // DEV - Use the upkeepID however you see fit
            return upkeepID;
        } else {
            revert("auto-approve disabled");
        }
    }
}
